import React, { useState } from 'react';
import { useSDK } from '@contentful/react-apps-toolkit';
import { Button, TextInput } from '@contentful/f36-components';

// REFACTOR CODE, services - FinSweet
// BONUS: support multiple images
// NOTE: Do I need to support scrolling images, pagination?
const PIXABAY_API_KEY = '45979271-945d910b95e96769c55111ae5';

const Dialog = () => {
  const sdk = useSDK();  // Access the SDK inside the dialog
  const [query, setQuery] = useState('');
  const [images, setImages] = useState([]);

  // Fetch images from Pixabay API
  const searchImages = async () => {
    const response = await fetch(`https://pixabay.com/api/?key=${PIXABAY_API_KEY}&q=${encodeURIComponent(query)}`);
    const data = await response.json();
    setImages(data.hits);
  };

  // Handle image selection and close the dialog with the selected image URL
  const handleImageClick = (imageUrl) => {
    sdk.close(imageUrl);  // Pass the selected image URL back to the Field component
  };

  return (
    <div>
      <TextInput
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for images"
      />
      <Button onClick={searchImages}>Search Images</Button>

      <div>
        {images.map((image) => (
          <img
            key={image.id}
            src={image.previewURL}
            alt={image.tags}
            style={{ margin: '10px', cursor: 'pointer' }}
            onClick={() => handleImageClick(image.largeImageURL)}  // Pass the selected image URL
          />
        ))}
      </div>
    </div>
  );
};

export default Dialog;
