/** @jsxImportSource @emotion/react */
import React, { useState } from 'react';
import { useSDK } from '@contentful/react-apps-toolkit';
import { Button, TextInput } from '@contentful/f36-components';
import styled from '@emotion/styled';

// Constants
const PIXABAY_API_KEY = '45979271-945d910b95e96769c55111ae5';

// Styled Components using Emotion
const Container = styled.div`
  padding: 20px;
  overflow-y: auto;
`;

const SearchInput = styled(TextInput)`
  margin-bottom: 10px;
`;

const ImageGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 20px;
  margin-bottom: 10px;
`;

const Image = styled.img`
  cursor: pointer;
  width: 150px;
  height: 150px;
  object-fit: cover;
  border: ${(props) => (props.isSelected ? '3px solid blue' : 'none')};
  opacity: ${(props) => (props.isDisabled ? 0.5 : 1)};
  pointer-events: ${(props) => (props.isDisabled ? 'none' : 'auto')};
`;

const Message = styled.p`
  margin-top: 20px;
  color: #666;
`;

const ErrorMessage = styled.p`
  margin-top: 20px;
  color: red;
  font-weight: bold;
`;

const PaginationControls = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
`;

// Component
const Dialog = () => {
  const sdk = useSDK();
  const [query, setQuery] = useState('');
  const [images, setImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);
  const [page, setPage] = useState(1);  // Track current page
  const [totalHits, setTotalHits] = useState(0);  // Total number of images for the query
  const [isLoading, setIsLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);  // Track if the user has searched
  const [error, setError] = useState(null);  // Error state
  const { currentImages, maxImages } = sdk.parameters.invocation;
  const imagesPerPage = 20;  // Number of images to fetch per page

  const searchImages = async (newPage = 1) => {
    setIsLoading(true);
    setError(null);  // Reset error state before making a new request
    setHasSearched(true);  // Track that the user has performed a search

    try {
      const response = await fetch(
        `https://pixabay.com/api/?key=${PIXABAY_API_KEY}&q=${encodeURIComponent(query)}&page=${newPage}&per_page=${imagesPerPage}`
      );
      if (!response.ok) {
        throw new Error('Failed to fetch images');
      }
      const data = await response.json();
      if (data.hits.length === 0) {
        setImages([]);  // No images found for the query
      } else {
        setImages(data.hits);  // Set images for the current page
        setTotalHits(data.totalHits);  // Update total hits for the query
        setPage(newPage);  // Update the current page number
      }
    } catch (error) {
      setError('Something went wrong. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleImageClick = (imageUrl) => {
    const isSelected = selectedImages.includes(imageUrl);

    if (isSelected) {
      setSelectedImages(selectedImages.filter((img) => img !== imageUrl));
    } else if (selectedImages.length < maxImages) {
      setSelectedImages([...selectedImages, imageUrl]);
    }
  };

  const handleSubmit = () => {
    sdk.close(selectedImages);  // Return the selected images
  };

  const nextPage = () => {
    if (page * imagesPerPage < totalHits) {
      searchImages(page + 1);  // Fetch the next page of images
    }
  };

  const prevPage = () => {
    if (page > 1) {
      searchImages(page - 1);  // Fetch the previous page of images
    }
  };

  return (
    <Container>
      <SearchInput
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for images"
      />
      <Button onClick={() => searchImages(1)} isDisabled={isLoading}>
        {isLoading ? 'Searching...' : 'Search Images'}
      </Button>

      {error && <ErrorMessage>{error}</ErrorMessage>}  {/* Display error message if any */}

      {!error && images.length === 0 && hasSearched && !isLoading && (
        <Message>No images found for this query. Try a different search term.</Message>
      )}

      <ImageGrid>
        {images.map((image) => {
          const isAlreadySelected = currentImages.includes(image.largeImageURL);  // Check if image is already selected
          const isSelected = selectedImages.includes(image.largeImageURL);

          return (
            <Image
              key={image.id}
              src={image.previewURL}
              alt={image.tags}
              isSelected={isSelected}
              isDisabled={isAlreadySelected}  // Disable re-selection if image is already selected
              onClick={() => handleImageClick(image.largeImageURL)}
            />
          );
        })}
      </ImageGrid>

      {images.length > 0 && (
        <PaginationControls>
          <Button onClick={prevPage} isDisabled={page === 1 || isLoading}>
            Previous Page
          </Button>
          <Button
            onClick={nextPage}
            isDisabled={page * imagesPerPage >= totalHits || isLoading}
          >
            Next Page
          </Button>
        </PaginationControls>
      )}

      <Button onClick={handleSubmit} isDisabled={selectedImages.length === 0}>
        Submit {selectedImages.length} Image{selectedImages.length !== 1 && 's'}
      </Button>

      {selectedImages.length >= maxImages && (
        <Message>You can only select up to {maxImages} images.</Message>
      )}
    </Container>
  );
};

export default Dialog;




