/** @jsxImportSource @emotion/react */
import React, { useState } from 'react';
import { useSDK } from '@contentful/react-apps-toolkit';
import { Button, TextInput } from '@contentful/forma-36-react-components';
import styled from '@emotion/styled';

// Styled components using Emotion's `styled` API
const Container = styled.div`
  padding: 20px;
  max-height: 500px;
  overflow-y: auto;
`;

const ImageGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 20px;
`;

const Image = styled.img`
  cursor: pointer;
  width: 150px;
  height: 150px;
  object-fit: cover;
`;

const PIXABAY_API_KEY = '45979271-945d910b95e96769c55111ae5';

const Dialog = () => {
  const sdk = useSDK();
  const [query, setQuery] = useState('');
  const [images, setImages] = useState([]);

  const searchImages = async () => {
    const response = await fetch(`https://pixabay.com/api/?key=${PIXABAY_API_KEY}&q=${encodeURIComponent(query)}`);
    const data = await response.json();
    setImages(data.hits);
  };

  const handleImageClick = (imageUrl) => {
    sdk.close(imageUrl);
  };

  return (
    <Container>
      <TextInput
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for images"
      />
      <Button onClick={searchImages}>Search Images</Button>

      <ImageGrid>
        {images.map((image) => (
          <Image
            key={image.id}
            src={image.previewURL}
            alt={image.tags}
            onClick={() => handleImageClick(image.largeImageURL)}
          />
        ))}
      </ImageGrid>
    </Container>
  );
};

export default Dialog;




