/** @jsxImportSource @emotion/react */
import React from 'react';
import styled from '@emotion/styled';

const ImageGridWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 20px;
`;

const Image = styled.img`
  cursor: pointer;
  width: 150px;
  height: 150px;
  object-fit: cover;
  border: ${(props) => (props.isSelected ? '3px solid blue' : 'none')};
  opacity: ${(props) => (props.isDisabled ? 0.5 : 1)};
  pointer-events: ${(props) => (props.isDisabled ? 'none' : 'auto')};
`;

const ImageGrid = ({ images, onImageClick, currentImages, selectedImages }) => {
  return (
    <ImageGridWrapper>
      {images.map((image) => {
        const isAlreadySelected = currentImages.includes(image.largeImageURL);
        const isSelected = selectedImages.includes(image.largeImageURL);

        return (
          <Image
            key={image.id}
            src={image.previewURL}
            alt={image.tags}
            isSelected={isSelected}
            isDisabled={isAlreadySelected}
            onClick={() => onImageClick(image.largeImageURL)}
          />
        );
      })}
    </ImageGridWrapper>
  );
};

export default ImageGrid;
