import React, { useState, useEffect } from 'react';
import { useSDK } from '@contentful/react-apps-toolkit';
import { Button } from '@contentful/forma-36-react-components';

const Field = () => {
  const sdk = useSDK();  // Access the Contentful SDK
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {
    // Load the initial value from the field (if exists)
    const initialValue = sdk.field.getValue();
    if (initialValue) {
      setImageUrl(initialValue);
    }
  }, [sdk]);

  // Open dialog to select an image from Pixabay
  const openPixabayDialog = async () => {
    const selectedImageUrl = await sdk.dialogs.openCurrentApp({
      title: 'Select an Image from Pixabay',
      width: 'large',
    });

    // After the dialog closes, we get the selected image URL
    if (selectedImageUrl) {
      setImageUrl(selectedImageUrl);
      sdk.field.setValue(selectedImageUrl);  // Store the selected image URL in the field
    }
  };

  // Remove the image
  const removeImage = () => {
    setImageUrl(null);
    sdk.field.removeValue();  // Clear the field value
  };

  return (
    <div>
      {imageUrl ? (
        <>
          <img src={imageUrl} alt="Selected from Pixabay" style={{ maxWidth: '100%' }} />
          <Button onClick={removeImage}>Remove Image</Button>
        </>
      ) : (
        <Button onClick={openPixabayDialog}>Select Image</Button>
      )}
    </div>
  );
};

export default Field;
