import React, { useState } from 'react';
import { useSDK } from '@contentful/react-apps-toolkit';
import { Button, TextInput } from '@contentful/forma-36-react-components';

const Dialog = () => {
  const sdk = useSDK();  // Access the SDK inside the dialog
  const [query, setQuery] = useState('');
  const [images, setImages] = useState([]);

  // Fetch images from Pixabay API
  const searchImages = async () => {
    const response = await fetch(`https://pixabay.com/api/?key=YOUR_PIXABAY_API_KEY&q=${encodeURIComponent(query)}`);
    const data = await response.json();
    setImages(data.hits);
  };

  return (
    <div>
      <TextInput
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for images"
      />
      <Button onClick={searchImages}>Search Images</Button>

      <div>
        {images.map((image) => (
          <img
            key={image.id}
            src={image.previewURL}
            alt={image.tags}
            style={{ margin: '10px', cursor: 'pointer' }}
            onClick={() => sdk.close(image.largeImageURL)}  // Pass selected image URL back to parent
          />
        ))}
      </div>
    </div>
  );
};

export default Dialog;
